const PersonStats = ({ item }) => {
  console.log("Přijaté osobní statistiky:", item);

  if (!item) {
      return <p>Žádná data k dispozici</p>;
  }

  return (
      <div>
          <h2>Statistiky společností</h2>
          <table className="table table-bordered">
              <thead>
                  <tr>
                      <th>Letošní rok</th>
                      <th>Celkový výdělek</th>
                      <th>Počet faktur</th>
                  </tr>
              </thead>
              <tbody>
                  <tr>
                      <td>{item.currentYearSum ?? 0}</td>
                      <td>{item.allTimeSum ?? 0}</td>
                      <td>{item.invoicesCount ?? 0}</td> {/* Opraveno na správný klíč */}
                  </tr>
              </tbody>
          </table>
      </div>
  );
};

export default PersonStats;
