const InvoiceStats = ({ items = [] }) => {
    console.log("Přijaté fakturační statistiky:", items);

    if (!Array.isArray(items)) {
        console.error("Chyba: `items` není pole!", items);
        return <p>Chyba při načítání dat.</p>;
    }

    return (
        <div>
            <h2>Statistiky faktur</h2>
            {items.length === 0 ? (
                <p>Žádná data k dispozici</p>
            ) : (
                <table className="table table-bordered">
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Jméno/Název</th>
                            <th>Výdělek</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items.map((item, index) => (
                            <tr key={index + 1}>
                                <td>{index + 1}</td>
                                <td>{item.personName || "Neznámé jméno"}</td>
                                <td>{item.revenue ?? 0}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default InvoiceStats;
