import React, { useEffect, useState } from "react";
import InvoiceStats from "./InvoiceStats";
import PersonStats from "./PersonStats";
import { apiGet } from "../utils/api"; // Nezapomeňte správně importovat API funkci

const StatsIndex = () => {
    const [personsStats, setPersonsStats] = useState([]);
    const [personalStats, setPersonalStats] = useState([]); // Opravený překlep

    useEffect(() => {
        apiGet("/api/persons/statistics")
            .then((data) => setPersonsStats(data || []))
            .catch((error) => console.error("Chyba při načítání statistik osob:", error));
    }, []);

    useEffect(() => {
        apiGet("/api/invoices/statistics")
            .then((data) => setPersonalStats(data || []))
            .catch((error) => console.error("Chyba při načítání statistik faktur:", error));
    }, []);

    return (
        <div>
            <h1>Statistiky společnosti</h1>
            <InvoiceStats items={personalStats} />

            <h1>Statistiky společností</h1>
            <PersonStats items={personsStats} />
        </div>
    );
}

export default StatsIndex;
