import React, { useEffect, useState } from "react";
import { apiDelete, apiGet } from "../utils/api";
import InvoiceTable from "./InvoiceTable";

const InvoiceIndex = () => {
    const [invoices, setInvoices] = useState([]);
    const [filters, setFilters] = useState({
        buyerID: "",
        sellerID: "",
        product: "",
        minPrice: "",
        maxPrice: "",
    });

    // Načítání faktur s filtry
    const fetchInvoices = async () => {
        try {
            // Filtrujeme prázdné hodnoty
            const filteredParams = Object.fromEntries(
                Object.entries(filters).filter(([_, v]) => v !== "")
            );
    
            // Správná syntaxe URL s &
            const queryParams = new URLSearchParams(filteredParams).toString();
            
            const url = queryParams ? `/api/invoices?${queryParams}` : "/api/invoices";
            const data = await apiGet(url);
            
            setInvoices(data);
        } catch (error) {
            console.error("Chyba při načítání faktur:", error);
        }
    };
    

    // Načítání faktur při spuštění a při změně filtrů
    useEffect(() => {
        fetchInvoices();
    }, []);

    const handleFilterChange = (e) => {
        setFilters({ ...filters, [e.target.name]: e.target.value });
    };

    const handleFilterSubmit = (e) => {
        e.preventDefault();
        fetchInvoices();
    };

    const deleteInvoice = async (id) => {
        try {
            await apiDelete(`/api/invoices/${id}`);
            setInvoices(invoices.filter((item) => item._id !== id));
        } catch (error) {
            console.log(error.message);
            alert(error.message);
        }
    };

    return (
        <div>
            <h1>Seznam faktur</h1>

            {/* Formulář pro filtry */}
            <form onSubmit={handleFilterSubmit}>
                <input
                    type="text"
                    name="buyerID"
                    value={filters.buyerID}
                    onChange={handleFilterChange}
                    placeholder="ID kupujícího"
                />
                <input
                    type="text"
                    name="sellerID"
                    value={filters.sellerID}
                    onChange={handleFilterChange}
                    placeholder="ID prodejce"
                />
                <input
                    type="text"
                    name="product"
                    value={filters.product}
                    onChange={handleFilterChange}
                    placeholder="Produkt"
                />
                <input
                    type="number"
                    name="minPrice"
                    value={filters.minPrice}
                    onChange={handleFilterChange}
                    placeholder="Minimální cena"
                />
                <input
                    type="number"
                    name="maxPrice"
                    value={filters.maxPrice}
                    onChange={handleFilterChange}
                    placeholder="Maximální cena"
                />
                <button type="submit">Filtrovat</button>
            </form>

            {/* Tabulka faktur */}
            <InvoiceTable
                deleteInvoice={deleteInvoice}
                items={invoices}
                label="Počet faktur: "
            />
        </div>
    );
};

export default InvoiceIndex;
