import React, {useEffect, useState} from "react";

import {apiGet} from "../utils/api";

const SalesPurchasesTable = (identificationNumber) => {
    const [sales, setSales] = useState([]);

    useEffect(() => {
        apiGet("/api/identification" + identificationNumber + "/sales").then((salesData) => {
            setSales(salesData);
        })
        .catch((error) => {
            console.log(error);
        })
    }, []);

    return (
        <div>
            <p>Počer vystavených faktur: {sales.lenght}</p>
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Číslo faktury</th>
                        <th>Datum</th>
                        <th>Celková částka</th>
                    </tr>
                </thead>
                <tbody>
                    {sales.map((sales, index) => {
                    const salesTotalAmount = sale.price + (sale.price * (sale.vat / 100));

                    const formattedDate = new Date(sale.issued).toLocaleDateString();
                    
