import React, { useEffect, useState } from "react";
import { apiDelete, apiGet } from "../utils/api";
import InvoiceTable from "./InvoiceTable";

const InvoiceIndex = () => {
    const [invoices, setInvoices] = useState([]);

    // üîπ Funkce pro z√≠sk√°n√≠ faktur s mo≈ænost√≠ filtrov√°n√≠
    const fetchInvoices = async (filters = {}) => {
        let query = Object.entries(filters)
            .filter(([_, value]) => value !== "") // Odstran√≠ pr√°zdn√© filtry
            .map(([key, value]) => `${key}=${encodeURIComponent(value)}`)
            .join("&");

        let url = "/api/invoices";
        if (query) {
            url += `?${query}`;
        }

        try {
            const data = await apiGet(url);
            setInvoices(data);
        } catch (error) {
            console.error("Chyba p≈ôi naƒç√≠t√°n√≠ faktur:", error);
        }
    };

    const deleteInvoice = async (id) => {
        try {
            await apiDelete(`/api/invoices/${id}`);
            setInvoices((prev) => prev.filter((item) => item._id !== id));
        } catch (error) {
            console.log(error.message);
            alert(error.message);
        }
    };

    useEffect(() => {
        fetchInvoices(); // ‚úÖ Naƒçte faktury p≈ôi startu
    }, []);

    return (
        <div>
            <h1>Seznam faktur</h1>
            <InvoiceTable
                deleteInvoice={deleteInvoice}
                items={invoices}
                label="Poƒçet faktur: "
                onFilter={fetchInvoices} // ‚úÖ P≈ôed√°v√°me funkci pro filtraci
            />
        </div>
    );
};

export default InvoiceIndex;
