import React, { useState, useEffect } from "react";
import { apiGet, apiPost } from "../utils/api";
import InputField from "../components/InputField";
import FlashMessage from "../components/FlashMessage";

const InvoiceForm = () => {
    const [invoice, setInvoice] = useState({
        invoiceNumber: "",
        date: "",
        dueDate: "",
        totalAmount: "",
        items: "",
        note: "",
        seller: null,
        buyer: null,
    });

    const [persons, setPersons] = useState([]);
    const [sentState, setSent] = useState(false);
    const [successState, setSuccess] = useState(false);
    const [errorState, setError] = useState(null);

    const fetchPersons = async () => {
        try {
            const response = await apiGet("/api/persons");
            console.log("Načtené osoby:", response); 
            setPersons(response);
        } catch (error) {
            console.error("Chyba při načítání osob:", error);
            setError("Chyba při načítání osob.");
        }
    };

    useEffect(() => {
        fetchPersons(); // Načítání osob při načtení komponenty
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();

        // Připravíme data k odeslání
        const invoiceData = {
            invoiceNumber: invoice.invoiceNumber,
            seller: invoice.seller ? { _id: invoice.seller } : null,
            buyer: invoice.buyer ? { _id: invoice.buyer } : null,
            issued: invoice.date,
            dueDate: invoice.dueDate,
            product: invoice.items,
            price: parseFloat(invoice.totalAmount),
            vat: 21,
            note: invoice.note,
        };

        apiPost("/api/invoices", invoiceData)
            .then(() => {
                setSent(true);
                setSuccess(true);
            })
            .catch((error) => {
                console.error("Chyba při odesílání faktury:", error);
                setError("Chyba při odesílání faktury.");
                setSent(true);
                setSuccess(false);
            });
    };

    return (
        <div>
            <h1>Vytvořit novou fakturu</h1>
            <hr />
            {errorState && <div className="alert alert-danger">{errorState}</div>}
            {sentState && (
                <FlashMessage
                    theme={successState ? "success" : "danger"}
                    text={successState ? "Faktura byla úspěšně vytvořena." : "Došlo k chybě při vytvoření faktury."}
                />
            )}
            <form onSubmit={handleSubmit}>
                <InputField
                    required={true}
                    type="text"
                    name="invoiceNumber"
                    label="Číslo faktury"
                    value={invoice.invoiceNumber}
                    handleChange={(e) => setInvoice({ ...invoice, invoiceNumber: e.target.value })}
                />
                <InputField
                    required={true}
                    type="date"
                    name="date"
                    label="Datum vydání"
                    value={invoice.date}
                    handleChange={(e) => setInvoice({ ...invoice, date: e.target.value })}
                />
                <InputField
                    required={true}
                    type="date"
                    name="dueDate"
                    label="Datum splatnosti"
                    value={invoice.dueDate}
                    handleChange={(e) => setInvoice({ ...invoice, dueDate: e.target.value })}
                />
                <InputField
                    required={true}
                    type="number"
                    name="totalAmount"
                    label="Celková částka"
                    value={invoice.totalAmount}
                    handleChange={(e) => setInvoice({ ...invoice, totalAmount: e.target.value })}
                />
                <InputField
                    required={true}
                    type="text"
                    name="items"
                    label="Položky"
                    value={invoice.items}
                    handleChange={(e) => setInvoice({ ...invoice, items: e.target.value })}
                />
                <InputField
                    type="text"
                    name="note"
                    label="Poznámka"
                    value={invoice.note}
                    handleChange={(e) => setInvoice({ ...invoice, note: e.target.value })}
                />

                {/* Výběr prodávajícího */}
                <div className="form-group">
                    <label htmlFor="seller">Prodávající</label>
                    <select
                        id="seller"
                        className="form-control"
                        value={invoice.seller || ""}
                        onChange={(e) => setInvoice({ ...invoice, seller: e.target.value })}
                    >
                        <option value="">Vyberte prodávajícího</option>
                        {persons && persons.length > 0 ? (
                            persons.map((person) => (
                                <option key={person._id} value={person._id}>
                                    {person.name} - IČO: {person.identificationNumber} {/* Zobrazení jména a IČO */}
                                </option>
                            ))
                        ) : (
                            <option disabled>Načítání osob...</option>
                        )}
                    </select>
                </div>

                {/* Výběr kupujícího */}
                <div className="form-group">
                    <label htmlFor="buyer">Kupující</label>
                    <select
                        id="buyer"
                        className="form-control"
                        value={invoice.buyer || ""}
                        onChange={(e) => setInvoice({ ...invoice, buyer: e.target.value })}
                    >
                        <option value="">Vyberte kupujícího</option>
                        {persons && persons.length > 0 ? (
                            persons.map((person) => (
                                <option key={person._id} value={person._id}>
                                    {person.name} - IČO: {person.identificationNumber} {/* Zobrazení jména a IČO */}
                                </option>
                            ))
                        ) : (
                            <option disabled>Načítání osob...</option>
                        )}
                    </select>
                </div>

                {/* Tlačítko pro odeslání formuláře */}
                <button type="submit" className="btn btn-primary">Vytvořit fakturu</button>
            </form>
        </div>
    );
};

export default InvoiceForm;
