import React, { useEffect, useState } from "react";
import InvoiceStats from "./InvoiceStats";
import PersonStats from "./PersonStats";
import { apiGet } from "../utils/api";

const StatsIndex = () => {
    const [personStats, setPersonStats] = useState(null);
    const [invoiceStats, setInvoiceStats] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const personsData = await apiGet("/api/persons/statistics");
                console.log("Osobní statistiky:", personsData);
                setPersonStats(personsData || []);

                const invoicesData = await apiGet("/api/invoices/statistics");
                console.log("Fakturační statistiky:", invoicesData);
                setInvoiceStats(invoicesData || []);
            } catch (error) {
                console.error("Chyba při načítání statistik:", error);
            }
        };

        fetchData();
    }, []);

    return (
        <div>
            <h1>Statistiky</h1>
            {invoiceStats === null ? <p>Načítání fakturačních statistik...</p> : <InvoiceStats items={invoiceStats} />}
            {personStats === null ? <p>Načítání osobních statistik...</p> : <PersonStats items={personStats} />}
        </div>
    );
};

export default StatsIndex;
