import React, { useEffect, useState } from "react";
import { apiDelete, apiGet } from "../utils/api";
import InvoiceTable from "./InvoiceTable";

const InvoiceIndex = () => {
    const [invoices, setInvoices] = useState([]);
    const [filters, setFilters] = useState({
        buyerID: "",
        sellerID: "",
        product: "",
        minPrice: "",
        maxPrice: "",
    });

    const API_BASE_URL = "http://localhost:8080/api"; // Opravená základní URL

    const fetchInvoices = async () => {
        try {
            // Správné sestavení query parametrů
            const params = new URLSearchParams();
            params.append("buyerID", 1); // Použij skutečné hodnoty
    
            const url = `${API_BASE_URL}/invoices?${params.toString()}`;
            console.log("Fetching:", url); // Debugging
    
            const response = await fetch(url, {
                method: "GET",
                headers: { "Accept": "application/json" }
            });
    
            if (!response.ok) {
                const errorText = await response.text();
                throw new Error(`HTTP Error ${response.status}: ${errorText}`);
            }
    
            const data = await response.json();
            setInvoices(data);
        } catch (error) {
            console.error("Chyba při načítání faktur:", error);
        }
    };
    
    
    
    
    

    // Načítání faktur při spuštění a při změně filtrů
    useEffect(() => {
        fetchInvoices();
    }, []);

    const handleFilterChange = (e) => {
        setFilters({ ...filters, [e.target.name]: e.target.value });
    };

    const handleFilterSubmit = (e) => {
        e.preventDefault();
        fetchInvoices();
    };

    const deleteInvoice = async (id) => {
        try {
            await apiDelete(`/api/invoices/${id}`);
            setInvoices(invoices.filter((item) => item._id !== id));
        } catch (error) {
            console.log(error.message);
            alert(error.message);
        }
    };

    return (
        <div>
            <h1>Seznam faktur</h1>

            {/* Formulář pro filtry */}
            <form onSubmit={handleFilterSubmit}>
                <input
                    type="text"
                    name="buyerID"
                    value={filters.buyerID}
                    onChange={handleFilterChange}
                    placeholder="ID kupujícího"
                />
                <input
                    type="text"
                    name="sellerID"
                    value={filters.sellerID}
                    onChange={handleFilterChange}
                    placeholder="ID prodejce"
                />
                <input
                    type="text"
                    name="product"
                    value={filters.product}
                    onChange={handleFilterChange}
                    placeholder="Produkt"
                />
                <input
                    type="number"
                    name="minPrice"
                    value={filters.minPrice}
                    onChange={handleFilterChange}
                    placeholder="Minimální cena"
                />
                <input
                    type="number"
                    name="maxPrice"
                    value={filters.maxPrice}
                    onChange={handleFilterChange}
                    placeholder="Maximální cena"
                />
                <button type="submit">Filtrovat</button>
            </form>

            {/* Tabulka faktur */}
            <InvoiceTable
                deleteInvoice={deleteInvoice}
                items={invoices}
                label="Počet faktur: "
            />
        </div>
    );
};

export default InvoiceIndex;
