import React, { useEffect, useState } from "react";
import InvoiceStats from "./InvoiceStats";
import PersonStats from "./PersonStats";
import { apiGet } from "../utils/api";

const StatsIndex = () => {
    const [personStats, setPersonStats] = useState([]);
    const [invoiceStats, setInvoiceStats] = useState([]); // Opravený název

    useEffect(() => {
        const fetchData = async () => {
            try {
                const personsData = await apiGet("/api/persons/statistics");
                setPersonStats(Array.isArray(personsData) ? personsData : []); // Ověření, že API vrací pole

                const invoicesData = await apiGet("/api/invoices/statistics");
                setInvoiceStats(Array.isArray(invoicesData) ? invoicesData : []); // Ověření, že API vrací pole
            } catch (error) {
                console.error("Chyba při načítání statistik:", error);
            }
        };

        fetchData();
    }, []);

    return (
        <div>
            <h1>Statistiky společnosti</h1>
            <InvoiceStats items={invoiceStats} />

            <h1>Statistiky společností</h1>
            <PersonStats items={personStats} />
        </div>
    );
};

export default StatsIndex;
