import React, { useState } from "react";
import { Link } from "react-router-dom";

const InvoiceTable = ({ label, items, deleteInvoice, onFilter }) => {
    const [filters, setFilters] = useState({
        buyerID: "",
        sellerID: "",
        product: "",
        minPrice: "",
        maxPrice: "",
    });

    const handleFilterChange = (e) => {
        setFilters({
            ...filters,
            [e.target.name]: e.target.value,
        });
    };

    const handleSearch = () => {
        onFilter(filters);
    };

    return (
        <div>
            <h2>{label} ({items.length})</h2>

            {/* 🔍 Filtrování */}
            <div className="mb-3">
                <input type="text" name="buyerID" placeholder="ID kupujícího" className="form-control mb-2" onChange={handleFilterChange} />
                <input type="text" name="sellerID" placeholder="ID prodávajícího" className="form-control mb-2" onChange={handleFilterChange} />
                <input type="text" name="product" placeholder="Produkt" className="form-control mb-2" onChange={handleFilterChange} />
                <input type="number" name="minPrice" placeholder="Min. cena" className="form-control mb-2" onChange={handleFilterChange} />
                <input type="number" name="maxPrice" placeholder="Max. cena" className="form-control mb-2" onChange={handleFilterChange} />
                <button onClick={handleSearch} className="btn btn-primary">Filtrovat</button>
            </div>

            {/* 📜 Tabulka faktur */}
            <table className="table table-bordered">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Číslo faktury</th>
                        <th>Datum</th>
                        <th>Celková částka</th>
                        <th>Akce</th>
                    </tr>
                </thead>
                <tbody>
                    {items.map((item, index) => {
                        const totalAmount = item.price + (item.price * (item.vat / 100));
                        const formattedDate = new Date(item.issued).toLocaleDateString();

                        return (
                            <tr key={index + 1}>
                                <td>{index + 1}</td>
                                <td>{item.invoiceNumber}</td>
                                <td>{formattedDate}</td>
                                <td>{totalAmount.toFixed(2)}</td>
                                <td>
                                    <div className="btn-group">
                                        <Link to={`/invoices/show/${item._id}`} className="btn btn-sm btn-info">
                                            Zobrazit
                                        </Link>
                                        <Link to={`/invoices/edit/${item._id}`} className="btn btn-sm btn-warning">
                                            Upravit
                                        </Link>
                                        <button onClick={() => deleteInvoice(item._id)} className="btn btn-sm btn-danger">
                                            Odstranit
                                        </button>
                                    </div>
                                </td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>

            <Link to="/invoices/create" className="btn btn-success">Nová faktura</Link>
        </div>
    );
};

export default InvoiceTable;
