const PersonStats = ({ items = [] }) => {
  console.log("Přijaté osobní statistiky:", items);

  if (!Array.isArray(items)) {
      console.error("Chyba: `items` není pole!", items);
      return <p>Chyba při načítání dat.</p>;
  }

  return (
      <div>
          <h2>Statistiky společností</h2>
          {items.length === 0 ? (
              <p>Žádná data k dispozici</p>
          ) : (
              <table className="table table-bordered">
                  <thead>
                      <tr>
                          <th>Letošní rok</th>
                          <th>Celkový výdělek</th>
                          <th>Počet faktur</th>
                      </tr>
                  </thead>
                  <tbody>
                      {items.map((item, index) => (
                          <tr key={index + 1}>
                              <td>{item.currentYearSum ?? 0}</td>
                              <td>{item.allTimeSum ?? 0}</td>
                              <td>{item.invoiceCount ?? 0}</td>
                          </tr>
                      ))}
                  </tbody>
              </table>
          )}
      </div>
  );
};

export default PersonStats;
