import React, { useEffect, useState } from "react";
import InvoiceStats from "./InvoiceStats";
import PersonStats from "./PersonStats";
import { apiGet } from "../utils/api";

const StatsIndex = () => {
    const [personStats, setPersonStats] = useState(null); // Změněno na objekt (ne pole)
    const [invoiceStats, setInvoiceStats] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                // Načtení osobních statistik (vrací OBJEKT, ne pole)
                const personsData = await apiGet("/api/persons/statistics");
                console.log("Přijaté osobní statistiky:", personsData);
                setPersonStats(personsData ?? null); // Uložíme jako objekt nebo `null` pokud je prázdné

                // Načtení fakturačních statistik (vrací POLE)
                const invoicesData = await apiGet("/api/invoices/statistics");
                console.log("Přijaté fakturační statistiky:", invoicesData);
                setInvoiceStats(Array.isArray(invoicesData) ? invoicesData : []);
            } catch (error) {
                console.error("Chyba při načítání statistik:", error);
            }
        };

        fetchData();
    }, []);

    return (
        <div>
            <h1>Statistiky společnosti</h1>
            <InvoiceStats items={invoiceStats} />

            <h1>Statistiky společností</h1>
            {personStats ? <PersonStats item={personStats} /> : <p>Načítání...</p>}
        </div>
    );
};

export default StatsIndex;
