import React, { useEffect, useState } from "react";
import { apiGet } from "../utils/api";

const PersonStats = () => {
  const [personStat, setPersonStat] = useState([]);

  useEffect(() => {
    apiGet("/api/persons/statistics")
      .then((data) => setPersonStat(data))
      .catch((error) => {
        console.error("Chyba při načítání statistik:", error);
      });
  }, []);

  return (
    <div>
      <h2>Statistiky</h2>
      <table className="table table-bordered">
        <thead>
          <tr>
            <th>#</th>
            <th>Jméno/Název</th>
            <th>Výdělek</th>
          </tr>
        </thead>
        <tbody>
          {personStat
            .filter((item) => !item.hidden) // Filtrace skrytých položek
            .map((item, index) => (
              <tr key={index + 1}>
                <td>{index + 1}</td>
                <td>{item.personName}</td>
                <td>{item.revenue}</td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
};

export default PersonStats;
