import React from "react";

const PersonStats = ({ items = [] }) => {
  // Vybrat první záznam, pokud existuje
  const stats = items.length > 0 ? items[0] : null;

  return (
    <div>
      <h2>Statistiky osob</h2>
      {stats ? (
        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Letošní rok</th>
              <th>Celkový výdělek</th>
              <th>Počet faktur</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{stats.currentYearSum ?? 0}</td>
              <td>{stats.allTimeSum ?? 0}</td>
              <td>{stats.invoiceCount ?? 0}</td>
            </tr>
          </tbody>
        </table>
      ) : (
        <p>Žádná data k dispozici</p>
      )}
    </div>
  );
};

export default PersonStats;
